  function CollapseAllElements() {
    for (const elemId of collapsable_div_ids)
    {
        elem = document.getElementById(elemId)
        if(elem)
        {
            elem.classList.remove("show");
            //switch - to + in card header buttons
            button = document.querySelector("#heading" + elemId + " h2 button")
            button.classList.remove("show")
        }
        else
        {
            console.error("id '" + elemId + "' does not exist")
        }
    }
  }
  function ExpandAllElements() {
    for (const elemId of collapsable_div_ids)
    {
        elem = document.getElementById(elemId)
        if(elem)
        {
            elem.classList.add("show");
            //switch + to - in card header buttons
            button = document.querySelector("#heading" + elemId + " h2 button")
            button.classList.add("show")
        }
        else
        {
            console.error("id '" + elemId + "' does not exist")
        }
    }
  }

  function UnhideAllFlavors() {
    for (const elemId of allFlavorSelectDivs)
    {
        elem = document.getElementById(elemId)
        if(elem)
        {
            elem.classList.remove("deactivated");
        }
        else
        {
            console.error("id '" + elemId + "' does not exist")
        }
    }
    for (const elemId of ["flavor-hw-f", "flavor-hw-l", "flavor-sw-f", "flavor-sw-l"])
    {
        elem = document.getElementById(elemId)
        if(elem)
        {
            elem.classList.add("btn-selct-flavor-unselected");
            elem.classList.remove("btn-selct-flavor-selected");
        }
        else
        {
            console.error("id '" + elemId + "' does not exist")
        }
    }
  }

  function SelectSeFlavor(elems, btn) {
    UnhideAllFlavors()
    for (const elemId of elems)
    {
        elem = document.getElementById(elemId)
        if(elem)
        {
            elem.classList.add("deactivated");
        }
        else
        {
            console.error("id '" + elemId + "' does not exist")
        }
    }
    console.log("hiding " + elems.length + " entries")
    elem = document.getElementById(btn)
    if(elem)
    {
        elem.classList.remove("btn-selct-flavor-unselected");
        elem.classList.add("btn-selct-flavor-selected");
    }
    else
    {
        console.error("id '" + elemId + "' does not exist")
    }
  }

  function SelectHwFullFlavor() { SelectSeFlavor(div_ids_hide_if_hw_full, "flavor-hw-f") }
  function SelectHwLiteFlavor() { SelectSeFlavor(div_ids_hide_if_hw_lite, "flavor-hw-l") }
  function SelectSwFullFlavor() { SelectSeFlavor(div_ids_hide_if_sw_full, "flavor-sw-f") }
  function SelectSwLiteFlavor() { SelectSeFlavor(div_ids_hide_if_sw_lite, "flavor-sw-l") }
